import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

}
<div class="card">
  <h4>Login</h4>
  <div class="field">
      <label for="username">Username</label>
      <input id="username" type="text" class="text-base text-color surface-overlay p-2 border-1 border-solid surface-border border-round appearance-none outline-none focus:border-primary w-40" aria-describedby="username-help">
      <small id="username-help">Enter your username to reset your password.</small>
  </div>
</div>
